INSERT INTO `tushuxinxi` VALUES ('7', '2104191708290', '人生最美是清欢', 'upload/13b3a00e19cc41d3f2fc028f063a8309.png', '5', '林清玄', '北京十月文艺出版社', '100', '83.00', '<ul class=\"parameter2 p-parameter-list\">\n<li title=\"北京十月文艺出版社\">出版社：&nbsp;<a title=\"北京十月文艺出版社\" href=\"https://book.jd.com/publish/%E5%8C%97%E4%BA%AC%E5%8D%81%E6%9C%88%E6%96%87%E8%89%BA%E5%87%BA%E7%89%88%E7%A4%BE_1.html\" target=\"_blank\" rel=\"noopener\">北京十月文艺出版社</a></li>\n<li title=\"9787530215401\">ISBN：9787530215401</li>\n<li title=\"1\">版次：1</li>\n<li title=\"11865852\">商品编码：11865852</li>\n<li title=\"新经典\">品牌：<a href=\"https://www.jd.com/pinpai/1-1713-117753.html\">新经典</a></li>\n<li title=\"平装\">包装：平装</li>\n<li title=\"16开\">开本：16开</li>\n<li title=\"2016-03-01\">出版时间：2016-03-01</li>\n<li title=\"雅致纸\">用纸：雅致纸</li>\n<li title=\"268\">页数：268</li>\n<li title=\"中文\">正文语种：中文</li>\n<li title=\"中文\"><img src=\"https://img30.360buyimg.com/vc/jfs/t1984/42/2419925266/339423/6ecdc80a/56dfbba5N19d8d721.jpg\" /></li>\n</ul>', '2022-05-23 20:34:14');
