INSERT INTO `tushuxinxi` VALUES ('10', '2104191710302', '世界二十大名著', 'upload/b0f23ec9322c5abaf0a2402bf5259640.png', '5', '托尔斯泰', '可爱舒图书专营店', '100', '70.00', '<ul class=\"parameter2 p-parameter-list\">\n<li title=\"可爱舒图书专营店\">店铺：&nbsp;<a href=\"https://mall.jd.com/index-718841.html?from=pc\" target=\"_blank\" rel=\"noopener\">可爱舒图书专营店</a></li>\n<li title=\"线装书局\">出版社：&nbsp;<a title=\"线装书局\" href=\"https://book.jd.com/publish/%E7%BA%BF%E8%A3%85%E4%B9%A6%E5%B1%80_1.html\" target=\"_blank\" rel=\"noopener\">线装书局</a></li>\n<li title=\"9787512020061\">ISBN：9787512020061</li>\n<li title=\"19357683204\">商品编码：19357683204</li>\n<li title=\"精装\">包装：精装</li>\n<li title=\"16\">开本：16</li>\n<li title=\"2016-04-29\">出版时间：2016-04-29</li>\n<li title=\"胶版纸\">用纸：胶版纸</li>\n<li title=\"20\">套装数量：20</li>\n<li title=\"20\"><img src=\"https://img30.360buyimg.com/imgzone/jfs/t1/142962/27/2319/130185/5f040f98E54348452/fb58dfcbbc758a2e.jpg\" /><img src=\"https://img14.360buyimg.com/imgzone/jfs/t20263/313/1059351720/70426/7006eb99/5b1f30b7N151ec8bd.jpg\" /><img src=\"https://img11.360buyimg.com/imgzone/jfs/t21031/172/1047219786/64043/235e8ba0/5b1f30b7N36540d5e.jpg\" /><img src=\"https://img12.360buyimg.com/imgzone/jfs/t20224/11/1037707276/68028/8b85b947/5b1f30b7N285f569f.jpg\" /></li>\n</ul>', '2022-05-23 23:10:43');
INSERT INTO `tushuxinxi` VALUES ('11', '2104191711164', '我喜欢生命本来的样子', 'upload/31308877a22751a45533f1eabc6c99e3.png', '5', '周国平', '源创新出版物专营店', '100', '40.00', '<ul class=\"parameter2 p-parameter-list\">\n<li title=\"源创新出版物专营店\">店铺：&nbsp;<a href=\"https://ycxchina.jd.com/\" target=\"_blank\" rel=\"noopener\">源创新出版物专营店</a></li>\n<li title=\"作家出版社\">出版社：&nbsp;<a title=\"作家出版社\" href=\"https://book.jd.com/publish/%E4%BD%9C%E5%AE%B6%E5%87%BA%E7%89%88%E7%A4%BE_1.html\" target=\"_blank\" rel=\"noopener\">作家出版社</a></li>\n<li title=\"9787506391542\">ISBN：9787506391542</li>\n<li title=\"12697619829\">商品编码：12697619829</li>\n<li title=\"源创新\">品牌：<a href=\"https://www.jd.com/pinpai/1-1713-593339.html\">源创新</a></li>\n<li title=\"胶版纸\">用纸：胶版纸</li>\n<li title=\"304\">页数：304</li>\n<li title=\"中文\">正文语种：中文</li>\n<li title=\"中文\">\n<div id=\"detail-tag-id-3\" class=\"book-detail-item\">\n<div class=\"item-mc\">\n<div class=\"book-detail-content\">　在茫茫宇宙间，每个人都只有一次生存的机会，都是不可重复的存在。名声、财产、知识等等是身外之物，人人都可求而得之，但没有人能够代替你感受人生。你死之后，没有人能够代替你再活一次。那么仅有一次的人生到底应该怎么度过？周国平说，每个个体要尊重生命本来的样子，当好自然之子。<br />　　当面对爱、逆境、孤独、死亡、变故等人生大命题的时候，人应当保持在什么心理状态，是《我喜欢生命本来的样子》的意义所在。希望《我喜欢生命本来的样子》能为在繁杂的世界里找不到头绪的你，建立强大的灵魂世界。<br />　　《我喜欢生命本来的样子》为哲学家周国平先生毕生散文精华，全书采用四色印刷，配以大量精美的图片，在完美的视觉体验中感悟生命的智慧。</div>\n</div>\n</div>\n<div id=\"detail-tag-id-4\" class=\"book-detail-item\">\n<div class=\"item-mt\">\n<h3>作者简介</h3>\n</div>\n<div class=\"item-mc\">\n<div class=\"book-detail-content\">　　周国平，当代学者、作家。出版有散文集《守望的距离》《各自的朝圣路》《安静》《善良丰富高贵》《愿生命从容》等，以哲思和文采著称，寓哲理于常情中，深入浅出，平易之中多见理趣，多年来深受读者喜爱。<br />　　周国平的散文总有一种白玉兰一般的芳香，清清爽爽的，干干净净的。无论什么时候读他的散文，都可以使人感受到从喧嚣的闹市，飞到若禅若道的平静世界，从烦恼丛生的惆怅，回到无根无欲的无邪时代。徜徉在他优美的文字中，总会逆溯到一份难得的清醒。</div>\n</div>\n</div>\n<div id=\"detail-tag-id-6\" class=\"book-detail-item\">\n<div class=\"item-mt\">\n<h3>目录</h3>\n</div>\n<div class=\"item-mc\">\n<div class=\"book-detail-content\">自序 人生的三个觉醒<br />Chapter 1 生命本没有意义<br />生命到底有没有意义<br />闲适：享受生命本身<br />救世和自救<br />点与面<br />面对苦难<br />知道自己要什么<br />相貌和心灵<br />比成功更宝贵的<br />消费=享受？<br /><br />Chapter 2 当好自然之子<br />不较劲的智慧<br />当好自然之子<br />逆境也是生活<br />不和时间赛跑<br />珍惜和放下<br />父母怎样对孩子负责<br /><br />Chapter 3 恰到好处的爱<br />爱是没有理由的心疼<br />爱，从痴迷到依恋<br />男人眼中的女人<br />爱情不风流<br />婚姻中的爱情<br />婚姻是难事<br />宽松的婚姻<br />性爱五题<br />宽容偶然的出轨行为<br />伴侣之情<br />异性之间能有纯粹的友谊吗？<br /><br />Chapter 4 信仰是生命的归宿<br />信仰之光<br />度一个创造的人生<br />人所能及的神圣<br />d一重要的是做人<br />真、善、美<br />朋友<br />己所欲，勿施于人<br />人是需要一点精神的<br />真正的友谊不是感情投资<br />在义与利之外<br /><br />Chapter 5 灵魂只能独行<br />人人都是孤儿<br />孤独的价值<br />独处也是一种能力<br />独处的充实<br />在黑暗中并肩行走<br />认识你自己<br />自由的灵魂<br />纯真的心性<br /><br />Chapter 6 思考死，有意义的徒劳<br />生病与觉悟<br />思考死：有意义的徒劳<br />人生贵在行胸臆<br />关于死亡的思绪<br />哭铁生<br /><br />Chapter 7 成为你自己<br />自我二重奏<br />珍惜平凡的生活<br />用好自己的性格<br />正确的财富观<br />正确的财富观（下）<br />快乐工作的能力</div>\n</div>\n</div>\n</li>\n</ul>', '2022-05-23 18:44:07');
