INSERT INTO `tushuxinxi` VALUES ('16', '2104191716092', '走着走着在春天', 'upload/ce11d72e5d1c6fbba8b461d449e8fd97.png', '6', '张晓风', '现代出版社', '100', '65.00', '<ul class=\"parameter2 p-parameter-list\">\n<li title=\"梅雅轩精品图书专营店\">店铺：&nbsp;<a href=\"https://myxts.jd.com/\" target=\"_blank\" rel=\"noopener\">梅雅轩精品图书专营店</a></li>\n<li title=\"现代出版社\">出版社：&nbsp;<a title=\"现代出版社\" href=\"https://book.jd.com/publish/%E7%8E%B0%E4%BB%A3%E5%87%BA%E7%89%88%E7%A4%BE_1.html\" target=\"_blank\" rel=\"noopener\">现代出版社</a></li>\n<li title=\"9787514383102\">ISBN：9787514383102</li>\n<li title=\"69938337566\">商品编码：69938337566</li>\n<li title=\"69938337566\"><img src=\"http://img12.360buyimg.com/imgzone/jfs/t1/137380/14/713/135011/5ed0c86dE5e751fc3/cbe8ad3ae37e3d28.png\" /></li>\n<li title=\"69938337566\">\" 张晓风笔若太阳之热,霜雪之贞。其散文作品宁静致远、淡泊明净。精细、秀气、典雅间洋溢着浓的化不开的情,有厚的举不动的爱。 本书精选张晓风各个时期的散文代表作品数十篇,包括入选国内语文教材的《我喜欢》《行道树》《只因为年轻啊》和入选中高考试卷的《不知有花》《母的羽衣》等多篇经典作品。一本书阅尽张晓风散文精华。 \"</li>\n</ul>', '2022-05-23 18:56:23');
