  `xingbie` varchar(10) NOT NULL COMMENT '性别',
  `shoujihaoma` varchar(50) NOT NULL COMMENT '手机号码',
  `shengri` varchar(25) NOT NULL COMMENT '生日',
  `suozaichengshi` varchar(50) NOT NULL COMMENT '所在城市',
  `gexingqianming` varchar(255) NOT NULL COMMENT '个性签名',
  `shouhuodizhi` varchar(255) NOT NULL COMMENT '收货地址',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb4 COMMENT='用户';

